mapper.update:
update ${prefix}ACT_CMMN_CASEDEF

  REV_ = #{revisionNext, jdbcType=INTEGER},    
  CATEGORY_ = #{category, jdbcType=VARCHAR},

where ID_ = #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}

mapper.select:
select * 
from ${prefix}ACT_CMMN_CASEDEF 
where DEPLOYMENT_ID_ = #{deploymentId}
  and KEY_ = #{caseDefinitionKey}
  and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * 
from ${prefix}ACT_CMMN_CASEDEF 
where DEPLOYMENT_ID_ = #{deploymentId}
  and KEY_ = #{caseDefinitionKey}
  and TENANT_ID_ = #{tenantId}

select *
from ${prefix}ACT_CMMN_CASEDEF DEF
where EXISTS (select 1 from ${prefix}ACT_CMMN_DEPLOYMENT D where D.PARENT_DEPLOYMENT_ID_ = #{parentDeploymentId} and D.ID_ = DEF.DEPLOYMENT_ID_)
  and KEY_ = #{caseDefinitionKey}
  and TENANT_ID_ = #{tenantId}

select *
from ${prefix}ACT_CMMN_CASEDEF 
where KEY_ = #{parameter.caseDefinitionKey} and VERSION_ = #{parameter.caseDefinitionVersion} and TENANT_ID_ = #{parameter.tenantId}

mapper.sql:

from ${prefix}ACT_CMMN_CASEDEF RES


    RES.ID_ = #{id}


    and RES.ID_ in

      #{item}



    and RES.CATEGORY_ = #{category}


    and RES.CATEGORY_ like #{categoryLike}${wildcardEscapeClause}


    and ( RES.CATEGORY_ <> #{categoryNotEquals} OR RES.CATEGORY_ is null )


    and RES.NAME_ = #{name}


    and RES.NAME_ like #{nameLike}${wildcardEscapeClause}


    and RES.KEY_ = #{key}


    and RES.KEY_ like #{keyLike}${wildcardEscapeClause}


    and RES.RESOURCE_NAME_ = #{resourceName}


    and RES.RESOURCE_NAME_ like #{resourceNameLike}${wildcardEscapeClause}


    and RES.VERSION_ = #{version}


    and RES.VERSION_ > #{versionGt}


    and RES.VERSION_ >= #{versionGte}


    and RES.VERSION_ < #{versionLt}


    and RES.VERSION_ <= #{versionLte}


    and RES.DEPLOYMENT_ID_ = #{deploymentId}


    and RES.DEPLOYMENT_ID_ in

      #{item}



    and RES.VERSION_ = (select max(VERSION_) from ${prefix}ACT_CMMN_CASEDEF where KEY_ = RES.KEY_ 

           and TENANT_ID_ = #{tenantId}


           and TENANT_ID_ like #{tenantIdLike}${wildcardEscapeClause}


           and (TENANT_ID_ = '' or TENANT_ID_ is null)


           and ( (TENANT_ID_ IS NOT NULL and TENANT_ID_ = RES.TENANT_ID_) or (TENANT_ID_ IS NULL and RES.TENANT_ID_ IS NULL) )

    )


    and RES.TENANT_ID_ = #{tenantId}


    and RES.TENANT_ID_ like #{tenantIdLike}${wildcardEscapeClause}


    and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)


      AND


             exists (select ID_  from ${prefix}ACT_RU_IDENTITYLINK  IDN where IDN.SCOPE_DEFINITION_ID_ = RES.ID_ and IDN.SCOPE_TYPE_ = 'cmmn' and IDN.USER_ID_ = #{authorizationUserId})


              OR exists (select ID_ from ${prefix}ACT_RU_IDENTITYLINK  IDN where IDN.SCOPE_DEFINITION_ID_ = RES.ID_ and IDN.SCOPE_TYPE_ = 'cmmn' and 
                (


                    or

                    IDN.GROUP_ID_ IN

                      #{group}


                )
              )
