mapper.update:
update ${prefix}ACT_CMMN_RU_CASE_INST
set
  LOCK_TIME_ = #{lockTime, jdbcType=TIMESTAMP},
  LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}
where ID_ = #{id}
  and (LOCK_TIME_ is null OR LOCK_TIME_ < #{expirationTime, jdbcType=TIMESTAMP})

mapper.select:
select C.*, P.ID_ as PI_ID_, P.REV_ as PI_REV_, P.CASE_DEF_ID_ as PI_CASE_DEF_ID_, P.DERIVED_CASE_DEF_ID_ as PI_DERIVED_CASE_DEF_ID_, P.CASE_INST_ID_ as PI_CASE_INST_ID_, P.STAGE_INST_ID_ as PI_STAGE_INST_ID_, P.IS_STAGE_ as PI_IS_STAGE_, P.ELEMENT_ID_ as PI_ELEMENT_ID_, P.ITEM_DEFINITION_ID_ as PI_ITEM_DEFINITION_ID_, P.ITEM_DEFINITION_TYPE_ as PI_ITEM_DEFINITION_TYPE_, P.NAME_ as PI_NAME_, P.STATE_ as PI_STATE_, P.CREATE_TIME_ as PI_CREATE_TIME_, P.LAST_AVAILABLE_TIME_ as PI_LAST_AVAILABLE_TIME_, P.LAST_ENABLED_TIME_ as PI_LAST_ENABLED_TIME_, P.LAST_DISABLED_TIME_ as PI_LAST_DISABLED_TIME_, P.LAST_STARTED_TIME_ as PI_LAST_STARTED_TIME_, P.LAST_SUSPENDED_TIME_ as PI_LAST_SUSPENDED_TIME_, P.COMPLETED_TIME_ as PI_COMPLETED_TIME_, P.OCCURRED_TIME_ as PI_OCCURRED_TIME_ , P.TERMINATED_TIME_ as PI_TERMINATED_TIME_, P.EXIT_TIME_ as PI_EXIT_TIME_, P.ENDED_TIME_ as PI_ENDED_TIME_, P.START_USER_ID_ as PI_USER_ID_, P.REFERENCE_ID_ as PI_REFERENCE_ID_, P.REFERENCE_TYPE_ as PI_REFERENCE_TYPE_, P.IS_COMPLETEABLE_ as PI_IS_COMPLETEABLE_, P.ENTRY_CRITERION_ID_ as PI_ENTRY_CRITERION_ID_, P.EXIT_CRITERION_ID_ as PI_EXIT_CRITERION_ID_, P.EXTRA_VALUE_ as PI_EXTRA_VALUE_, P.IS_COUNT_ENABLED_ as PI_IS_COUNT_ENABLED_, P.VAR_COUNT_ as PI_VAR_COUNT_, P.SENTRY_PART_INST_COUNT_ as PI_SENTRY_PART_INST_COUNT_, P.TENANT_ID_ as PI_TENANT_ID_, CASE_DEF.KEY_ as CaseDefinitionKey, CASE_DEF.NAME_ as CaseDefinitionName, CASE_DEF.VERSION_ as CaseDefinitionVersion, CASE_DEF.DEPLOYMENT_ID_ as CaseDefinitionDeploymentId from ${prefix}ACT_CMMN_RU_CASE_INST C inner join ${prefix}ACT_CMMN_CASEDEF CASE_DEF on C.CASE_DEF_ID_ = CASE_DEF.ID_ left join ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST P on C.ID_ = CASE_INST_ID_ and P.ENDED_TIME_ is null
C.ID_ = #{caseInstanceId, jdbcType=VARCHAR}
and C.ID_ = (select CASE_INST_ID_ from ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST where ID_ = #{planItemInstanceId, jdbcType=VARCHAR})

${limitBefore}
select RES.* ${limitBetween},
    CASE_DEF.KEY_ as CaseDefinitionKey,
    CASE_DEF.NAME_ as CaseDefinitionName,
    CASE_DEF.VERSION_ as CaseDefinitionVersion,
    CASE_DEF.DEPLOYMENT_ID_ as CaseDefinitionDeploymentId
from ${prefix}ACT_CMMN_RU_CASE_INST RES

inner join ${prefix}ACT_CMMN_CASEDEF CASE_DEF on RES.CASE_DEF_ID_ = CASE_DEF.ID_

${orderBy}
${limitAfter}

where.if:
and EXISTS(select ID_ from ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST P where P.CASE_INST_ID_ = RES.ID_ and P.ITEM_DEFINITION_ID_ in

    #{activePlanItemDefinitionId}

and P.STATE_ = 'active')

and EXISTS(
select ID_ from ${prefix}ACT_RU_IDENTITYLINK I where I.SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'cmmn' and I.GROUP_ID_ in

    #{involvedGroup}

)

choose.otherwise:
and exists (
    select 1
    from ${prefix}ACT_RU_VARIABLE V

        RES.ID_ = V.SCOPE_ID_
        and V.SUB_SCOPE_ID_ is null
        and V.SCOPE_TYPE_ = 'cmmn'


            and V.NAME_= #{queryVariableValue.name}


            and V.TYPE_ = #{queryVariableValue.type}




                    and (lower(V.TEXT_)


                    and (V.TEXT_



                LIKE


            #{queryVariableValue.textValue}

                ${wildcardEscapeClause}


                or V.TEXT_ is null

            )


            and V.TEXT2_

                LIKE


            #{queryVariableValue.textValue2}

                ${wildcardEscapeClause}



            and (V.LONG_

            #{queryVariableValue.longValue}

                or V.LONG_ is null

            )


            and (V.DOUBLE_

            #{queryVariableValue.doubleValue}

                or V.DOUBLE_ is null

            )





                    and (V.TEXT_ is not null or V.TEXT2_ is not null or V.LONG_ is not null or V.DOUBLE_ is not null or V.BYTEARRAY_ID_ is not null)


                    and .TEXT_ is null and V.TEXT2_ is null and V.LONG_ is null and V.DOUBLE_ is null and V.BYTEARRAY_ID_ is null




)

where.foreach:
                and



                        or RES.ID_ = #{orQueryObject.caseInstanceId}


                        or RES.ID_ IN

                            #{caseInstanceId}



                        or RES.CASE_DEF_ID_ = #{orQueryObject.caseDefinitionId}


                        or CASE_DEF.KEY_ = #{orQueryObject.caseDefinitionKey}


                        or RES.CASE_DEF_ID_ IN

                            #{caseDefinitionId}



                        or CASE_DEF.KEY_ IN

                            #{caseDefinitionKey}



                        or CASE_DEF.CATEGORY_ = #{orQueryObject.caseDefinitionCategory}


                        or CASE_DEF.NAME_ = #{orQueryObject.caseDefinitionName}


                        or CASE_DEF.VERSION_ = #{orQueryObject.caseDefinitionVersion}


                        or RES.PARENT_ID_ = #{orQueryObject.caseInstanceParentId}


                        or RES.BUSINESS_KEY_ = #{orQueryObject.businessKey}


                        or RES.START_TIME_ <= #{orQueryObject.startedBefore}


                        or RES.START_TIME_ >= #{orQueryObject.startedAfter}


                        or RES.START_USER_ID_ = #{orQueryObject.startedBy}


                        or RES.LAST_REACTIVATED_TIME_ <= #{lastReactivatedBefore}


                        or RES.LAST_REACTIVATED_TIME_ >= #{lastReactivatedAfter}


                        or RES.LAST_REACTIVATED_USER_ID_ = #{lastReactivatedBy}


                        or RES.CALLBACK_ID_ = #{orQueryObject.callbackId}


                        or RES.CALLBACK_TYPE_ = #{orQueryObject.callbackType}


                        or RES.REFERENCE_ID_ = #{orQueryObject.referenceId}


                        or RES.REFERENCE_TYPE_ = #{orQueryObject.referenceType}


                        or RES.IS_COMPLETEABLE_ = #{orQueryObject.completeable, jdbcType=BOOLEAN}


                        or RES.TENANT_ID_ = #{orQueryObject.tenantId}


                        or RES.TENANT_ID_ like #{orQueryObject.tenantIdLike}


                        or (RES.TENANT_ID_ is null or RES.TENANT_ID_ = '')


		                or EXISTS(select ID_ from ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST P where P.CASE_INST_ID_ = RES.ID_ and P.ITEM_DEFINITION_ID_ = #{orQueryObject.activePlanItemDefinitionId} and P.STATE_ = 'active')


		                or EXISTS(select ID_ from ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST P where P.CASE_INST_ID_ = RES.ID_ and P.ITEM_DEFINITION_ID_ in

		                    #{activePlanItemDefinitionId}

		                and P.STATE_ = 'active')


                        or EXISTS(select ID_ from ${prefix}ACT_RU_IDENTITYLINK I where I.SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'cmmn' and I.USER_ID_ =
                        #{orQueryObject.involvedUser})


			            or EXISTS(select ID_ from ${prefix}ACT_RU_IDENTITYLINK I where I.SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'cmmn' and 
			            I.USER_ID_ = #{orQueryObject.involvedUserIdentityLink.userId} and I.TYPE_ = #{orQueryObject.involvedUserIdentityLink.type})


                        or EXISTS(
                        select ID_ from ${prefix}ACT_RU_IDENTITYLINK I where I.SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'cmmn' and I.GROUP_ID_ in

                            #{involvedGroup}

                        )


			            or EXISTS(select ID_ from ${prefix}ACT_RU_IDENTITYLINK I where I.SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'cmmn' and 
			            I.GROUP_ID_ = #{orQueryObject.involvedGroupIdentityLink.groupId} and I.TYPE_ = #{orQueryObject.involvedGroupIdentityLink.type})


                        or



                                    and EXISTS (select ID_ from ${prefix}ACT_RU_VARIABLE where NAME_ = #{queryVariableValue.name} and RES.ID_ = SCOPE_ID_ and
                                    SUB_SCOPE_ID_
                                    is null and SCOPE_TYPE_ = 'cmmn')


                                    and NOT EXISTS (select ID_ from ${prefix}ACT_RU_VARIABLE where NAME_ = #{queryVariableValue.name} and RES.ID_ = SCOPE_ID_
                                    and
                                    SUB_SCOPE_ID_ is null and SCOPE_TYPE_ = 'cmmn')


                                    and exists (
                                        select 1
                                        from ${prefix}ACT_RU_VARIABLE V

                                            RES.ID_ = V.SCOPE_ID_
                                            and V.SUB_SCOPE_ID_ is null
                                            and V.SCOPE_TYPE_ = 'cmmn'


                                                and V.NAME_= #{queryVariableValue.name}


                                                and V.TYPE_ = #{queryVariableValue.type}




                                                        and (lower(V.TEXT_)


                                                        and (V.TEXT_




                                                        LIKE





                                                #{queryVariableValue.textValue}


                                                        ${wildcardEscapeClause}



                                                    or A_OR${index}.TEXT_ is null

                                                )


                                                and V.TEXT2_

                                                    LIKE




                                                #{queryVariableValue.textValue2}

                                                    ${wildcardEscapeClause}



                                                and (V.LONG_

                                                #{queryVariableValue.longValue}

                                                    or A_OR${index}.LONG_ is null

                                                )


                                                and (V.DOUBLE_

                                                #{queryVariableValue.doubleValue}

                                                    or A_OR${index}.DOUBLE_ is null

                                                )





                                                        and (V.TEXT_ is not null or V.TEXT2_ is not null or V.LONG_ is not null or
                                                        V.DOUBLE_
                                                        is not null or V.BYTEARRAY_ID_ is not null)


                                                        and V.TEXT_ is null and V.TEXT2_ is null and V.LONG_ is null and
                                                        V.DOUBLE_ is null
                                                        and V.BYTEARRAY_ID_ is null




                                    )
