mapper.update:
update ${prefix}ACT_CMMN_RU_CASE_INST
set
  LOCK_TIME_ = #{lockTime, jdbcType=TIMESTAMP}
where ID_ = #{id}
  and (LOCK_TIME_ is null OR LOCK_TIME_ < #{expirationTime, jdbcType=TIMESTAMP})

update ${prefix}ACT_CMMN_RU_CASE_INST
set LOCK_TIME_ = null
where ID_ = #{id}

mapper.select:
select C.*, P.ID_ as PI_ID_, P.REV_ as PI_REV_, P.CASE_DEF_ID_ as PI_CASE_DEF_ID_, P.CASE_INST_ID_ as PI_CASE_INST_ID_, P.STAGE_INST_ID_ as PI_STAGE_INST_ID_, P.IS_STAGE_ as PI_IS_STAGE_, P.ELEMENT_ID_ as PI_ELEMENT_ID_, P.ITEM_DEFINITION_ID_ as PI_ITEM_DEFINITION_ID_, P.ITEM_DEFINITION_TYPE_ as PI_ITEM_DEFINITION_TYPE_, P.NAME_ as PI_NAME_, P.STATE_ as PI_STATE_, P.START_TIME_ as PI_START_TIME_, P.START_USER_ID_ as PI_USER_ID_, P.REFERENCE_ID_ as PI_REFERENCE_ID_, P.REFERENCE_TYPE_ as PI_REFERENCE_TYPE_, P.IS_COMPLETEABLE_ as PI_IS_COMPLETEABLE_, P.IS_COUNT_ENABLED_ as PI_IS_COUNT_ENABLED_, P.VAR_COUNT_ as PI_VAR_COUNT_, P.SENTRY_PART_INST_COUNT_ as PI_SENTRY_PART_INST_COUNT_, P.TENANT_ID_ as PI_TENANT_ID_ from ${prefix}ACT_CMMN_RU_CASE_INST C inner join ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST P on C.ID_ = CASE_INST_ID_ where
C.ID_ = #{caseInstanceId, jdbcType=VARCHAR}
C.ID_ = (select CASE_INST_ID_ from ${prefix}ACT_CMMN_RU_PLAN_ITEM_INST where ID_ = #{planItemInstanceId, jdbcType=VARCHAR})

${limitBefore}
select RES.* ${limitBetween}
from ${prefix}ACT_CMMN_RU_CASE_INST RES
inner join ${prefix}ACT_CMMN_CASEDEF CASE_DEF on RES.CASE_DEF_ID_ = CASE_DEF.ID_

${orderBy}
${limitAfter}
