mapper.select:
${limitBefore}
select distinct RES.* ${limitBetween},
    CASE_DEF.KEY_ as CaseDefinitionKey,
    CASE_DEF.NAME_ as CaseDefinitionName,
    CASE_DEF.VERSION_ as CaseDefinitionVersion,
    CASE_DEF.DEPLOYMENT_ID_ as CaseDefinitionDeploymentId

${orderBy}
${limitAfter}

if.if:
left outer join ${prefix}ACT_HI_VARINST A_OR${orIndex} on (RES.ID_ = A_OR${orIndex}.SCOPE_ID_ and
A_OR${orIndex}.SUB_SCOPE_ID_ is null and A_OR${orIndex}.SCOPE_TYPE_ = 'cmmn'
)

otherwise.if:


    and (lower(A${index}.TEXT_)


    and (A${index}.TEXT_



  LIKE


#{queryVariableValue.textValue}

  ${wildcardEscapeClause}


      or A${index}.TEXT_ is null

  )

where.foreach:
and





            or CASE_DEF_OR${orIndex}.KEY_ = #{orQueryObject.caseDefinitionKey}


            or CASE_DEF_OR${orIndex}.KEY_ IN

                #{caseDefinitionKey}



            or CASE_DEF_OR${orIndex}.CATEGORY_ = #{orQueryObject.caseDefinitionCategory}


            or CASE_DEF_OR${orIndex}.NAME_ = #{orQueryObject.caseDefinitionName}


            or CASE_DEF_OR${orIndex}.VERSION_ = #{orQueryObject.caseDefinitionVersion}


            or CASE_DEF_OR${orIndex}.DEPLOYMENT_ID_ = #{orQueryObject.deploymentId}


            or CASE_DEF_OR${orIndex}.DEPLOYMENT_ID_ IN

                #{deploymentId}



            or



                    and EXISTS (select ID_ from ${prefix}ACT_HI_VARINST where NAME_ = #{queryVariableValue.name} and RES.ID_ = SCOPE_ID_ and
                    SUB_SCOPE_ID_ is null and SCOPE_TYPE_ = 'cmmn')


                    and NOT EXISTS (select ID_ from ${prefix}ACT_HI_VARINST where NAME_ = #{queryVariableValue.name} and RES.ID_ = SCOPE_ID_ and
                    SUB_SCOPE_ID_ is null and SCOPE_TYPE_ = 'cmmn')




                        and A_OR${orIndex}.NAME_= #{queryVariableValue.name}


                        and A_OR${orIndex}.VAR_TYPE_ = #{queryVariableValue.type}




                                and (lower(A_OR${orIndex}.TEXT_)


                                and (A_OR${orIndex}.TEXT_



                            LIKE




                        #{queryVariableValue.textValue}

                            ${wildcardEscapeClause}


                            or A_OR${index}.TEXT_ is null

                        )


                        and A_OR${orIndex}.TEXT2_

                            LIKE


                        #{queryVariableValue.textValue2}

                            ${wildcardEscapeClause}



                        and (A_OR${orIndex}.LONG_

                        #{queryVariableValue.longValue}

                            or A_OR${index}.LONG_ is null

                        )


                        and (A_OR${index}.DOUBLE_

                        #{queryVariableValue.doubleValue}

                            or A_OR${index}.DOUBLE_ is null

                        )





                                and (A_OR${orIndex}.TEXT_ is not null or A_OR${orIndex}.TEXT2_ is not null or A_OR${orIndex}.LONG_ is not null or A_OR${orIndex}.DOUBLE_ is not null or A_OR${orIndex}.BYTEARRAY_ID_ is not null)


                                and A_OR${orIndex}.TEXT_ is null and A_OR${orIndex}.TEXT2_ is null and A_OR${orIndex}.LONG_ is null and A_OR${orIndex}.DOUBLE_ is null and A_OR${orIndex}.BYTEARRAY_ID_ is null

sql.if:
and EXISTS(select ID_ from ${prefix}ACT_CMMN_HI_PLAN_ITEM_INST P where P.CASE_INST_ID_ = ${queryTablePrefix}ID_ and P.ITEM_DEFINITION_ID_ in

    #{activePlanItemDefinitionId}

and P.STATE_ = 'active')

and EXISTS(
    select ID_ from ${prefix}ACT_HI_IDENTITYLINK I where I.SCOPE_ID_ = ${queryTablePrefix}ID_ and I.SCOPE_TYPE_ = 'cmmn' and I.GROUP_ID_ in

        #{involvedGroup}

)

or EXISTS(select ID_ from ${prefix}ACT_CMMN_HI_PLAN_ITEM_INST P where P.CASE_INST_ID_ = ${queryTablePrefix}ID_ and P.ITEM_DEFINITION_ID_ in

    #{activePlanItemDefinitionId}

and P.STATE_ = 'active')

or EXISTS(
select ID_ from ${prefix}ACT_HI_IDENTITYLINK I where I.SCOPE_ID_ = ${queryTablePrefix}ID_ and I.SCOPE_TYPE_ = 'cmmn' and I.GROUP_ID_ in

    #{involvedGroup}

)
