mapper.sql:
from ${prefix}ACT_CMMN_HI_PLAN_ITEM_INST RES


        RES.ID_ = #{planItemInstanceId}


        RES.NAME_ = #{planItemInstanceName}


        and RES.STATE_ = #{state}


        and RES.CASE_DEF_ID_ = #{caseDefinitionId}


        and RES.DERIVED_CASE_DEF_ID_ = #{derivedCaseDefinitionId}


        and RES.CASE_INST_ID_ = #{caseInstanceId}


        and RES.STAGE_INST_ID_ = #{stageInstanceId}


        and RES.ELEMENT_ID_ = #{elementId}


        and RES.ITEM_DEFINITION_ID_ = #{planItemDefinitionId}


        and RES.ITEM_DEFINITION_TYPE_ = #{planItemDefinitionType}


        and RES.ITEM_DEFINITION_TYPE_ in #{type}


        and RES.CREATE_TIME_ <= #{createdBefore}


        and RES.CREATE_TIME_ >= #{createdAfter}


        and RES.LAST_AVAILABLE_TIME_ <= #{lastAvailableBefore}


        and RES.LAST_AVAILABLE_TIME_ >= #{lastAvailableAfter}


        and RES.LAST_UNAVAILABLE_TIME_ <= #{lastUnavailableBefore}


        and RES.LAST_UNAVAILABLE_TIME_ >= #{lastUnavailableAfter}


        and RES.LAST_ENABLED_TIME_ <= #{lastEnabledBefore}


        and RES.LAST_ENABLED_TIME_ >= #{lastEnabledAfter}


        and RES.LAST_DISABLED_TIME_ <= #{lastDisabledBefore}


        and RES.LAST_DISABLED_TIME_ >= #{lastDisabledAfter}


        and RES.LAST_STARTED_TIME_ <= #{lastStartedBefore}


        and RES.LAST_STARTED_TIME_ >= #{lastStartedAfter}


        and RES.LAST_SUSPENDED_TIME_ <= #{lastSuspendedBefore}


        and RES.LAST_SUSPENDED_TIME_ >= #{lastSuspendedAfter}


        and RES.COMPLETED_TIME_ <= #{completedBefore}


        and RES.COMPLETED_TIME_ >= #{completedAfter}


        and RES.OCCURRED_TIME_ <= #{occurredBefore}


        and RES.OCCURRED_TIME_ >= #{occurredAfter}


        and RES.TERMINATED_TIME_ <= #{terminatedBefore}


        and RES.TERMINATED_TIME_ >= #{terminatedAfter}


        and RES.EXIT_TIME_ <= #{exitBefore}


        and RES.EXIT_TIME_ >= #{exitAfter}


        and RES.ENDED_TIME_ <= #{endedBefore}


        and RES.ENDED_TIME_ >= #{endedAfter}


        and RES.ENDED_TIME_ is not null


        and RES.ENDED_TIME_ is null


        and RES.START_USER_ID_ = #{startUserId}


        and RES.REFERENCE_ID_ = #{referenceId}


        and RES.REFERENCE_TYPE_ = #{referenceType}


        and RES.ENTRY_CRITERION_ID_ = #{entryCriterionId, jdbcType=VARCHAR}


        and RES.EXIT_CRITERION_ID_ = #{exitCriterionId, jdbcType=VARCHAR}


        and RES.EXTRA_VALUE_ = #{formKey, jdbcType=VARCHAR}


        and RES.EXTRA_VALUE_ = #{extraValue, jdbcType=VARCHAR}


        and RES.IS_STAGE_ = #{onlyStages, jdbcType=BOOLEAN}


        and exists(select I.ID_ from ${prefix}ACT_HI_IDENTITYLINK I where I.SUB_SCOPE_ID_ = RES.ID_ and I.SCOPE_TYPE_ = 'planItem'
            and
            (

                I.USER_ID_ = #{involvedUser}


                or


                (


                    or

                    I.GROUP_ID_ IN

                      #{group}


                )

            )
        )


        and RES.TENANT_ID_ = #{tenantId}


        and RES.TENANT_ID_ like #{tenantIdLike}


        and (RES.TENANT_ID_ is null or RES.TENANT_ID_ = '')
