        and RES.NAME_ = #{name, jdbcType=NVARCHAR}


        and RES.STATE_ = #{state, jdbcType=VARCHAR}


        and RES.CREATE_TIME_ <= #{createdBefore, jdbcType=TIMESTAMP}


        and RES.CREATE_TIME_ >= #{createdAfter, jdbcType=TIMESTAMP}


        and RES.LAST_AVAILABLE_TIME_ <= #{lastAvailableBefore, jdbcType=TIMESTAMP}


        and RES.LAST_AVAILABLE_TIME_ >= #{lastAvailableAfter, jdbcType=TIMESTAMP}


