mapper.update:
update ${prefix}ACT_DMN_DEPLOYMENT
CATEGORY_ = #{category, jdbcType=VARCHAR}, TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}, PARENT_DEPLOYMENT_ID_ = #{parentDeploymentId, jdbcType=VARCHAR},
where ID_ = #{id, jdbcType=VARCHAR}

mapper.sql:

from ${prefix}ACT_DMN_DEPLOYMENT RES


    RES.ID_ = #{deploymentId}


    and RES.NAME_ = #{name}


    and RES.NAME_ like #{nameLike}


    and RES.CATEGORY_ = #{category}


    and ( RES.CATEGORY_ <> #{categoryNotEquals} OR RES.CATEGORY_ is null )  


    and RES.TENANT_ID_ = #{tenantId}


    and RES.TENANT_ID_ like #{tenantIdLike}


    and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)


    and RES.PARENT_DEPLOYMENT_ID_ = #{parentDeploymentId}


    and RES.PARENT_DEPLOYMENT_ID_ like #{parentDeploymentIdLike}


    and exists(select 1 from ${prefix}ACT_DMN_DECISION DECISION where RES.ID_ = DECISION.DEPLOYMENT_ID_

      and DECISION.KEY_ = #{decisionKey}


      and DECISION.KEY_ like #{decisionKeyLike}

    )
