mapper.select:
	${limitBefore}
  select RES.*, DEF.KEY_ as DEC_DEF_KEY_, DEF.NAME_ as DEC_DEF_NAME_, DEF.VERSION_ as DEC_DEF_VERSION_ ${limitBetween}

  ${orderBy}
  ${limitAfter}

mapper.sql:

from ${prefix}ACT_DMN_HI_DECISION_EXECUTION RES
    inner join ${prefix}ACT_DMN_DECISION_TABLE DEF on RES.DECISION_DEFINITION_ID_ = DEF.ID_


    RES.ID_ = #{id}


    and RES.ID_ in

      #{item}



    and RES.DECISION_DEFINITION_ID_ = #{decisionDefinitionId}


    and RES.DEPLOYMENT_ID_ = #{deploymentId}


    and DEF.KEY_ = #{decisionKey}


    and RES.INSTANCE_ID_ = #{instanceId}


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.ACTIVITY_ID_ = #{activityId}


    and RES.SCOPE_TYPE_ = #{scopeType}


    and (RES.INSTANCE_ID_ = #{processInstanceIdWithChildren} or exists(select ELINK.ID_ from ${prefix}ACT_HI_ENTITYLINK ELINK where ELINK.LINK_TYPE_ = 'child' and 
        ELINK.SCOPE_ID_ = #{processInstanceIdWithChildren} AND ELINK.SCOPE_TYPE_ = 'bpmn' and 
        ELINK.REF_SCOPE_ID_ = RES.INSTANCE_ID_))


    and (RES.INSTANCE_ID_ = #{caseInstanceIdWithChildren} or exists(select ELINK.ID_ from ${prefix}ACT_HI_ENTITYLINK ELINK where ELINK.LINK_TYPE_ = 'child' and 
        ELINK.SCOPE_ID_ = #{caseInstanceIdWithChildren} AND ELINK.SCOPE_TYPE_ = 'cmmn' and 
        ELINK.REF_SCOPE_ID_ = RES.INSTANCE_ID_))


    and RES.FAILED_ = #{failed}


    and RES.TENANT_ID_ = #{tenantId}


    and RES.TENANT_ID_ like #{tenantIdLike}


    and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)
