mapper.select:
${limitBefore}
SELECT RES.* ${limitBetween}, DEF.KEY_ as PROC_DEF_KEY_, DEF.NAME_ as PROC_DEF_NAME_, DEF.VERSION_ as PROC_DEF_VERSION_, DEF.DEPLOYMENT_ID_ as DEPLOYMENT_ID_

${orderBy}
${limitAfter}

choose.otherwise:
and exists (
    select 1
    from ${prefix}ACT_HI_VARINST V
    
        RES.PROC_INST_ID_ = V.PROC_INST_ID_
        
            
            and V.NAME_= #{queryVariableValue.name}
        
        
            and V.VAR_TYPE_ = #{queryVariableValue.type}
        
        
            
                
                    and (lower(V.TEXT_)
                
                
                    and (V.TEXT_
                
            
            
                LIKE
                
            
            #{queryVariableValue.textValue}
            
                ${wildcardEscapeClause}
            
            
                or V.TEXT_ is null
            
            )
        
        
            and V.TEXT2_
            
                LIKE
                
            
            #{queryVariableValue.textValue2}
            
                ${wildcardEscapeClause}
            
        
        
            and (V.LONG_
            
            #{queryVariableValue.longValue}
            
                or V.LONG_ is null
            
            )
        
        
            and (V.DOUBLE_
            
            #{queryVariableValue.doubleValue}
            
                or V.DOUBLE_ is null
            
            )
        
        
        
            
                
                    and (V.TEXT_ is not null or V.TEXT2_ is not null or V.LONG_ is not null or V.DOUBLE_ is not null or V.BYTEARRAY_ID_ is not null)
                
                
                    and V.TEXT_ is null and V.TEXT2_ is null and V.LONG_ is null and V.DOUBLE_ is null and V.BYTEARRAY_ID_ is null
                
            
        
    
)

where.foreach:
and

  
    
  
  
    or DEF.KEY_ = #{orQueryObject.processDefinitionKey}
  
  
    or DEF.KEY_ IN
    
      #{definition}
    
  
  
    or DEF.VERSION_ = #{orQueryObject.processDefinitionVersion}
  
  
    or DEF.CATEGORY_ = #{orQueryObject.processDefinitionCategory}
  
  
    or DEF.NAME_ = #{orQueryObject.processDefinitionName}
  
  
    or DEF.DEPLOYMENT_ID_ = #{orQueryObject.deploymentId}
  
  
    or DEF.DEPLOYMENT_ID_ IN
    
      #{deployment}
    
  
  
    or
    
      
        and DEF.KEY_ not like #{procDefKey}${wildcardEscapeClause}
      
    
  
  
    or
    
      
        
            and EXISTS (select ID_ from ${prefix}ACT_HI_VARINST where NAME_ = #{queryVariableValue.name} and RES.PROC_INST_ID_ = PROC_INST_ID_)
        
        
            and NOT EXISTS (select ID_ from ${prefix}ACT_HI_VARINST where NAME_ = #{queryVariableValue.name} and RES.PROC_INST_ID_ = PROC_INST_ID_)
        
        
            and exists (
                select 1
                from ${prefix}ACT_HI_VARINST V
                
                    RES.PROC_INST_ID_ = PROC_INST_ID_
                    
                        
                        and V.NAME_= #{queryVariableValue.name}
                    
                    
                        and V.VAR_TYPE_ = #{queryVariableValue.type}
                    
                    
                        
                            
                                and (lower(V.TEXT_)
                            
                            
                                and (V.TEXT_
                            
                        
                        
                            LIKE
                            
                        
                        #{queryVariableValue.textValue}
                        
                            ${wildcardEscapeClause}
                        
                        
                            or A_OR${index}.TEXT_ is null
                        
                        )
                    
                    
                        and V.TEXT2_
                        
                            LIKE
                            
                        
                        #{queryVariableValue.textValue2}
                        
                            ${wildcardEscapeClause}
                        
                    
                    
                        and (V.LONG_
                        
                        #{queryVariableValue.longValue}
                        
                            or A_OR${index}.LONG_ is null
                        
                        )
                    
                    
                        and (V.DOUBLE_
                        
                        #{queryVariableValue.doubleValue}
                        
                            or A_OR${index}.DOUBLE_ is null
                        
                        )
                    
                    
                    
                        
                            
                                and (V.TEXT_ is not null or V.TEXT2_ is not null or V.LONG_ is not null or V.DOUBLE_ is not null or V.BYTEARRAY_ID_ is not null)
                            
                            
                                and V.TEXT_ is null and V.TEXT2_ is null and V.LONG_ is null and V.DOUBLE_ is null and V.BYTEARRAY_ID_ is null
                            
                        
                           
                
            )

where.if:
and exists ( select 1 from ${prefix}ACT_RU_TIMER_JOB JOB
RES.PROC_INST_ID_ = JOB.PROCESS_INSTANCE_ID_ and (JOB.EXCEPTION_MSG_ is not null or JOB.EXCEPTION_STACK_ID_ is not null)
)

sql.if:
and EXISTS(select ID_ from ${prefix}ACT_HI_ACTINST A where A.PROC_INST_ID_ = ${queryTablePrefix}ID_ and A.ACT_ID_ in 

    #{activeActivityId}

and A.END_TIME_ is null)

and EXISTS(
select ID_ from ${prefix}ACT_HI_IDENTITYLINK LINK where LINK.PROC_INST_ID_ = ${queryTablePrefix}ID_ and LINK.GROUP_ID_ in

    #{involvedGroup}

)

or EXISTS(select ID_ from ${prefix}ACT_HI_ACTINST A where A.PROC_INST_ID_ = ${queryTablePrefix}ID_ and A.ACT_ID_ in 

    #{activeActivityId}

and A.END_TIME_ is null)

or EXISTS(
      select ID_ from ${prefix}ACT_HI_IDENTITYLINK LINK where LINK.PROC_INST_ID_ = ${queryTablePrefix}ID_ and LINK.GROUP_ID_ in
      
          #{involvedGroup}
      
)
