mapper.sql:
    from ${prefix}ACT_RU_EVENT_SUBSCR RES


        RES.ID_ = #{id}


        and RES.EVENT_TYPE_ = #{eventType}


        and RES.EVENT_NAME_ = #{eventName}


        and RES.EXECUTION_ID_ = #{executionId}


        and RES.PROC_INST_ID_ = #{processInstanceId}


        and RES.PROC_DEF_ID_ = #{processDefinitionId}


        and RES.ACTIVITY_ID_ = #{activityId}


        and RES.SUB_SCOPE_ID_ = #{subScopeId}


        and RES.SCOPE_ID_ = #{scopeId}


        and RES.SCOPE_DEFINITION_ID_ = #{scopeDefinitionId}


        and RES.SCOPE_TYPE_ = #{scopeType}


		and RES.CREATED_ < #{createdBefore}


		and RES.CREATED_ > #{createdAfter}


        and RES.TENANT_ID_ = #{tenantId}


        and RES.TENANT_ID_ in

            #{item}



        and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)


          and RES.CONFIGURATION_ = #{configuration}


          and RES.CONFIGURATION_ in

              #{item}



          and RES.CONFIGURATION_ is null


          and


                  RES.ID_ = #{orQueryObject.id}


                  or RES.EVENT_TYPE_ = #{orQueryObject.eventType}


                  or RES.EVENT_NAME_ = #{orQueryObject.eventName}


                  or RES.EXECUTION_ID_ = #{orQueryObject.executionId}


                  or RES.PROC_INST_ID_ = #{orQueryObject.processInstanceId}


                  or RES.PROC_DEF_ID_ = #{orQueryObject.processDefinitionId}


                  or RES.ACTIVITY_ID_ = #{orQueryObject.activityId}


                  or RES.SUB_SCOPE_ID_ = #{orQueryObject.subScopeId}


                  or RES.SCOPE_ID_ = #{orQueryObject.scopeId}


                  or RES.SCOPE_DEFINITION_ID_ = #{orQueryObject.scopeDefinitionId}


                  or RES.SCOPE_TYPE_ = #{orQueryObject.scopeType}


                  or RES.CREATED_ < #{orQueryObject.createdBefore}


                  or RES.CREATED_ > #{orQueryObject.createdAfter}


                  or RES.TENANT_ID_ = #{orQueryObject.tenantId}


                  or RES.TENANT_ID_ in

                      #{item}



                  or (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)


                  or RES.CONFIGURATION_ = #{orQueryObject.configuration}


                  or RES.CONFIGURATION_ in

                      #{item}



                  or RES.CONFIGURATION_ is null

mapper.select:
select * 
from ${prefix}ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'message')
    and EVENT_NAME_ = #{parameter.eventName}  
    and PROC_INST_ID_ = #{parameter.processInstanceId}

select * from ${prefix}ACT_RU_EVENT_SUBSCR EVT left outer join ${prefix}ACT_RU_EXECUTION EXC on EVT.EXECUTION_ID_ = EXC.ID_ where EVENT_TYPE_ = 'signal' and EVENT_NAME_ = #{parameter.eventName} and ( (EVT.EXECUTION_ID_ is null) or (EVT.EXECUTION_ID_ is not null AND EXC.SUSPENSION_STATE_ = 1) )
and EVT.TENANT_ID_ = #{parameter.tenantId}
and (EVT.TENANT_ID_ = '' or EVT.TENANT_ID_ is null)

select * 
from ${prefix}ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'signal')
    and EVENT_NAME_ = #{parameter.eventName}  
    and PROC_INST_ID_ = #{parameter.processInstanceId}

select * 
from ${prefix}ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'signal')
    and EVENT_NAME_ = #{parameter.eventName}  
    and SCOPE_ID_ = #{parameter.scopeId}
    and SCOPE_TYPE_ = #{parameter.scopeType}

select * from ${prefix}ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = #{parameter.eventType}) and (EVENT_NAME_ = #{parameter.eventName})
and TENANT_ID_ = #{parameter.tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * from ${prefix}ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = 'message') and (EVENT_NAME_ = #{eventName}) and EXECUTION_ID_ is null
and TENANT_ID_ = #{tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

mapper.update:
update ${prefix}ACT_RU_EVENT_SUBSCR

  TENANT_ID_ = #{newTenantId, jdbcType=VARCHAR}

where TENANT_ID_ = #{oldTenantId, jdbcType=VARCHAR}

mapper.delete:
delete from ${prefix}ACT_RU_EVENT_SUBSCR where
SCOPE_DEFINITION_ID_ = #{scopeDefinitionId}
and SCOPE_TYPE_ = #{scopeType}
and SCOPE_ID_ is null
