mapper.sql:
from ${prefix}ACT_RU_TIMER_JOB RES


        RES.ID_ = #{id}


        and RES.PROCESS_INSTANCE_ID_ = #{processInstanceId}


        and RES.PROCESS_INSTANCE_ID_ IS NULL


        and RES.EXECUTION_ID_ = #{executionId}


        and RES.HANDLER_TYPE_ = #{handlerType}


        and RES.HANDLER_TYPE_ in

            #{item}



        and RES.PROC_DEF_ID_ = #{processDefinitionId}


        and RES.CATEGORY_ = #{category}


        and RES.CATEGORY_ like #{categoryLike}${wildcardEscapeClause}


        and RES.ELEMENT_ID_ = #{elementId}


        and RES.ELEMENT_NAME_ = #{elementName}


        and RES.SCOPE_ID_ = #{scopeId}


        and RES.SCOPE_ID_ IS NULL


        and RES.SUB_SCOPE_ID_ = #{subScopeId}


        and RES.SCOPE_TYPE_ = #{scopeType}


        and RES.SCOPE_DEFINITION_ID_ = #{scopeDefinitionId}


        and RES.CORRELATION_ID_ = #{correlationId}


        and RES.TYPE_ = 'timer'


        and RES.TYPE_ = 'message'


        and RES.DUEDATE_ <= #{now, jdbcType=TIMESTAMP}


        and RES.DUEDATE_ > #{duedateHigherThan}


        and RES.DUEDATE_ < #{duedateLowerThan}


        and RES.DUEDATE_ >= #{duedateHigherThanOrEqual}


        and RES.DUEDATE_ <= #{duedateLowerThanOrEqual}


        and (RES.EXCEPTION_MSG_ is not null or RES.EXCEPTION_STACK_ID_ is not null)


        and RES.EXCEPTION_MSG_ = #{exceptionMessage}


        and RES.TENANT_ID_ = #{tenantId}


        and RES.TENANT_ID_ like #{tenantIdLike}${wildcardEscapeClause}


        and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)


        and (RES.SCOPE_TYPE_ = '' or RES.SCOPE_TYPE_ is null)

mapper.select:
${limitBefore}
SELECT RES.* ${limitBetween}
from ${prefix}ACT_RU_TIMER_JOB RES
where 

    SCOPE_TYPE_ is null


    SCOPE_TYPE_ = #{parameter.jobExecutionScope}

and DUEDATE_ <= #{parameter.now, jdbcType=TIMESTAMP}
and LOCK_OWNER_ is null

    and CATEGORY_ in

        #{item}


${orderBy}
${limitAfter}

${limitBefore}
SELECT RES.* ${limitBetween}
from ${prefix}ACT_RU_TIMER_JOB RES


        SCOPE_TYPE_ is null


        SCOPE_TYPE_ = #{parameter.jobExecutionScope}

    and LOCK_EXP_TIME_ is not null and LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP}


        and CATEGORY_ in

            #{item}



${orderBy}
${limitAfter}

select J.*
from ${prefix}ACT_RU_TIMER_JOB J
where J.HANDLER_TYPE_ = #{parameter.handlerType}
and J.PROC_DEF_ID_ = #{parameter.processDefinitionId}

select J.*
from ${prefix}ACT_RU_TIMER_JOB J
inner join ${prefix}ACT_RE_PROCDEF P on J.PROC_DEF_ID_ = P.ID_
where J.HANDLER_TYPE_ = #{parameter.handlerType}
and P.KEY_ = #{parameter.processDefinitionKey}
and (P.TENANT_ID_ = '' or P.TENANT_ID_ is null)

select J.*
from ${prefix}ACT_RU_TIMER_JOB J
inner join ${prefix}ACT_RE_PROCDEF P on J.PROC_DEF_ID_ = P.ID_
where J.HANDLER_TYPE_ = #{parameter.handlerType}
and P.KEY_ = #{parameter.processDefinitionKey}
and P.TENANT_ID_ = #{parameter.tenantId}

select T.*
from ${prefix}ACT_RU_TIMER_JOB T
where T.SCOPE_ID_ = #{parameter.scopeId} and T.SUB_SCOPE_ID_ = #{parameter.subScopeId}

mapper.update:
update ${prefix}ACT_RU_TIMER_JOB set
TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where
ID_ in (
    SELECT J.ID_ from ${prefix}ACT_RU_TIMER_JOB J
    inner join ${prefix}ACT_RE_PROCDEF P on J.PROC_DEF_ID_ = P.ID_
    inner join ${prefix}ACT_RE_DEPLOYMENT D on P.DEPLOYMENT_ID_ = D.ID_
    where D.ID_ = #{deploymentId, jdbcType=VARCHAR}
)

update ${prefix}ACT_RU_TIMER_JOB set LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}
where ID_ in

    #{timerJob.id}

update ${prefix}ACT_RU_TIMER_JOB set
TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where
ID_ in (

SELECT tempTask.tempId
FROM (
    SELECT J.ID_ as tempId
    FROM ${prefix}ACT_RU_TIMER_JOB J
    inner join ${prefix}ACT_RE_PROCDEF P on J.PROC_DEF_ID_ = P.ID_
    inner join ${prefix}ACT_RE_DEPLOYMENT D on P.DEPLOYMENT_ID_ = D.ID_
    where D.ID_ = #{deploymentId, jdbcType=VARCHAR}
    ) AS tempTask
)

update ${prefix}ACT_RU_TIMER_JOB
set LOCK_OWNER_ = null, LOCK_EXP_TIME_ = null, CREATE_TIME_ = #{now, jdbcType=TIMESTAMP}
where ID_ = #{id,jdbcType=VARCHAR}
