    )


    or



            and EXISTS (
                select ID_ from ${prefix}ACT_HI_VARINST where NAME_ = #{queryVar.name}



                  and RES.PROC_INST_ID_ = PROC_INST_ID_


                  and RES.SCOPE_ID_ = SCOPE_ID_ AND SCOPE_TYPE_ = #{queryVar.scopeType}


