mapper.update:
update ${prefix}ACT_DE_MODEL
name = #{name, jdbcType=VARCHAR}, model_key = #{key, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR}, model_comment = #{comment, jdbcType=VARCHAR}, created = #{created, jdbcType=TIMESTAMP}, created_by = #{createdBy, jdbcType=VARCHAR}, last_updated = #{lastUpdated, jdbcType=TIMESTAMP}, last_updated_by = #{lastUpdatedBy, jdbcType=VARCHAR}, version = #{version, jdbcType=INTEGER}, model_editor_json = #{modelEditorJson, jdbcType=VARCHAR}, model_type = #{modelType, jdbcType=INTEGER}, thumbnail = #{thumbnail, jdbcType=${blobType}}, tenant_id = #{tenantId, jdbcType=VARCHAR}
where id = #{id, jdbcType=VARCHAR}

mapper.select:
select model.* from ${prefix}ACT_DE_MODEL_RELATION modelrelation 
inner join ${prefix}ACT_DE_MODEL model on modelrelation.model_id = model.id
where modelrelation.parent_model_id = #{parentModelId, jdbcType=VARCHAR}

select * from ${prefix}ACT_DE_MODEL


       model_type = #{modelType, jdbcType=VARCHAR}


       and (lower(name) like #{filter, jdbcType=VARCHAR} or lower(description) like #{filter, jdbcType=VARCHAR}) 


       and model_key = #{key, jdbcType=VARCHAR}


       and tenant_id = #{tenantId, jdbcType=VARCHAR}




        order by name asc


        order by name desc


        order by last_updated asc


        order by last_updated desc

select count(m.id) from ${prefix}ACT_DE_MODEL m 
where m.created_by = #{createdBy, jdbcType=VARCHAR} and m.model_type = #{modelType, jdbcType=INTEGER}

  and tenant_id = #{tenantId, jdbcType=VARCHAR}
