mapper.update:
update ${prefix}ACT_DE_MODEL_HISTORY
name = #{name, jdbcType=VARCHAR}, model_key = #{key, jdbcType=VARCHAR}, description = #{description, jdbcType=VARCHAR}, model_comment = #{comment, jdbcType=VARCHAR}, created = #{created, jdbcType=TIMESTAMP}, created_by = #{createdBy, jdbcType=VARCHAR}, last_updated = #{lastUpdated, jdbcType=TIMESTAMP}, last_updated_by = #{lastUpdatedBy, jdbcType=VARCHAR}, version = #{version, jdbcType=INTEGER}, model_editor_json = #{modelEditorJson, jdbcType=VARCHAR}, model_id = #{modelId, jdbcType=VARCHAR}, removal_date = #{removalDate, jdbcType=TIMESTAMP}, tenant_id = #{tenantId, jdbcType=VARCHAR}
where id = #{id, jdbcType=VARCHAR}

mapper.select:
select * from ${prefix}ACT_DE_MODEL_HISTORY 
where created_by = #{createdBy, jdbcType=VARCHAR}
and model_type = #{modelType, jdbcType=INTEGER}
and removal_date is null

and tenant_id = #{tenantId, jdbcType=VARCHAR}

select * from ${prefix}ACT_DE_MODEL_HISTORY 
where model_id = #{modelId, jdbcType=VARCHAR}
and removal_date is null
