        and RES.SCOPE_TYPE_ = #{scopeType}


        and RES.NAME_ = #{variableName}


        and RES.NAME_ like #{variableNameLike}${wildcardEscapeClause}





            and RES.VAR_TYPE_ = #{queryVariableValue.type}


